create sequence invoices_id_seq;
alter sequence invoices_id_seq owner to postgres;
create table invoices
(
    id             bigint  default nextval('invoices_id_seq'::regclass) not null
        constraint invoice_pkey
            primary key,
    datacreate     timestamp with time zone,
    department     varchar(20),
    comment        text,
    ordernumber    varchar(50),
    senttoapprove  boolean,
    senttopurchase boolean,
    invoicenumber  varchar,
    totalprice     double precision,
    resolveddate   timestamp with time zone,
    customerid     bigint,
    senttoprice    boolean default false
);


