create table purchases
(
    id                  bigint default nextval('purchases_id_seq'::regclass) not null
        constraint assetelements_pkey
            primary key,
    invoiceid           bigint                                               not null
        constraint invoiceid_fkey
            references invoices,
    nomenclatureid      bigint                                               not null,
    count               smallint,
    approver            varchar(255),
    resolvingdate       timestamp with time zone,
    comment             text,
    buyingprice         double precision,
    commentnumenclature text
);


